package cn.npnt.tiaps.assemble.vo;public class FriendCount {	private long followingCount;	private long followerCount;		public FriendCount(){			}	public FriendCount(long followingCount,long followerCount){		this.followerCount = followerCount;		this.followingCount = followingCount;	}	/**	 * @return the followingCount	 */	public long getFollowingCount() {		return followingCount;	}	/**	 * @param followingCount the followingCount to set	 */	public void setFollowingCount(long followingCount) {		this.followingCount = followingCount;	}	/**	 * @return the followerCount	 */	public long getFollowerCount() {		return followerCount;	}	/**	 * @param followerCount the followerCount to set	 */	public void setFollowerCount(long followerCount) {		this.followerCount = followerCount;	}	}