package cn.npnt.tiaps.base.vo;import java.io.Serializable;import cn.npnt.tiaps.base.constants.ApplicationConstant;public class BaseResponseVO implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 1L;	private HeaderVO header = new HeaderVO();	private Object body;		public BaseResponseVO(){	}		public BaseResponseVO(int ret , Exception e){		header.setRet(ret);		if(ApplicationConstant.IS_PUBLISHED_DEBUG){			header.setErrorMsg(e.toString() + "  message : "  +  e.getMessage());		}	}		public HeaderVO getHeader() {		return header;	}	public void setHeader(HeaderVO header) {		this.header = header;	}	public Object getBody() {		return body;	}	public void setBody(Object body) {		this.body = body;	}}