package cn.npnt.tiaps.dao.impl;import java.util.List;import org.apache.commons.lang.StringUtils;import cn.npnt.tiaps.base.dao.impl.HibernateGenericDao;import cn.npnt.tiaps.dao.TrendDao;import cn.npnt.tiaps.entity.Trend;/** *  * @company 新和新拓（北京）科技有限公司 * @author Recoba Gan * @createDate 2011 2011-12-18 下午4:06:11 * @description 活动的主题Dao层实现类 */public class HibernateTrendDaoImpl extends HibernateGenericDao<Trend, Long> implements		TrendDao {	@Override	public List<Trend> findByCondiiton(Trend trend) {		StringBuilder hql = new StringBuilder(" from Trend where 1=1 ");		if(trend != null){			if(StringUtils.isNotBlank(trend.getTitle())){				hql.append(" and title='").append(trend.getTitle()).append("' ");			}		}		return getHibernateTemplate().find(hql.toString());	}}