package cn.npnt.tiaps.service;import java.util.List;import java.util.Map;import org.apache.commons.fileupload.FileItem;import cn.npnt.tiaps.base.vo.QueryParamVO;import cn.npnt.tiaps.entity.Look;import cn.npnt.tiaps.entity.User;import cn.npnt.tiaps.vo.LookVO;/** * @company 新和新拓（北京）科技有限公司 * @author Recoba Gan * @createDate 2011 2011-12-22 下午1:09:59 * @description 微博业务接口 */public interface LookService {	/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-22 下午1:36:49	 * @description 创建一个look	 */	Look createLook(Look look,List<FileItem> files,String uploadPath, String writeURL) throws Exception;		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-25 上午9:00:06	 * @description 根据look id查找look对象	 */	Look getLookByPK(long lookId);		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-25 上午9:06:20	 * @description 删除look对象	 */	Look deleteLook(Look look);		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-25 下午10:28:59	 * @description 仲裁look，当发现look的verdict值为-1的时候，表示该look正等待仲裁，	 * 仲裁一个look的条件：当有大于等于20个人对该look进行了评价或者该look的发布时间两天过后，即需要对该look进行仲裁	 * 仲裁结果是喜欢的人大于等于不喜欢的人的时候，仲裁结果就是喜欢，喜欢第几张就是返回几。反之表示不喜欢，则verdict为0	 * 	 * -1表示还是未仲裁【等待仲裁】、0表示仲裁结果为不喜欢、1表示喜欢第一张、2表示喜欢第二张....	 * -2表示无结果	 */	byte disputeLook(Look look);		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-26 下午10:48:14	 * @description 获取当前传递的user的自己的微博和自己转发的微博	 * @param	user 这个user对象必须还有accessId或者id两个其中的一个即可	 */	List<Look> getUserLooks(User user , QueryParamVO paramVO)throws Exception;		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-26 下午11:32:52	 * @description 将一个look对象转换为起需要的VO对象	 */	LookVO convertToVO(Look look,User currUser);		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-28 上午10:38:04	 * @description 获取系统推荐的微博列表	 */	List<Look> getSystemRecommend(QueryParamVO param)throws Exception;		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-29 上午11:54:40	 * @description 获取timeline的首页信息（包括用户原创、转发、收听）	 */	List<Look> getTimeLineHome(QueryParamVO param,Map<String,Object> other)throws Exception;		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2012 2012-1-1 上午10:19:42	 * @description 转发一条look	 */	Look repostLook(Look look , Look reporterLook);		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2012 2012-1-8 下午6:26:32	 * @description 处理look的访问次数，times 正数表示加times，负数表示减times	 */	void manageLookVisitCount(Look look ,int times)throws Exception;		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2012 2012-1-11 上午3:03:15	 * @description 条件查询look	 */	List<Look> getSearchLook(QueryParamVO param , Map<String,Object> map);		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2012 2012-1-11 上午3:03:15	 * @description 条件查询look	 */	List<Look> getLookByPage(QueryParamVO param);		/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2012 2012-2-5 下午5:54:09	 * @description 获取一批用户发表的look集合，按照发表时间的倒序返回	 * 	 */	List<Look> getUsersLook(List<Long> userIdList,Map<String,Object> param);}