package cn.npnt.tiaps.service;import java.util.List;/** *  * @company 新和新拓（北京）科技有限公司 * @author newnewmac * @createDate 11:45:04 AM Jan 3, 2012 * @description 投票管理接口 */public interface VoteService {	/**	 * @company 新和新拓（北京）科技有限公司	 * @author Recoba Gan	 * @createDate 2011 2011-12-27 上午12:38:10	 * @description 根据lookId查询其各个图片评论总数 返回值map中，key 为图片在该组look中的下标，value表示投票的数量	 */	public abstract List<Integer> findLookVotedData(Long lookId);	/**	 * 	 * @company 新和新拓（北京）科技有限公司	 * @author newnewmac	 * @createDate 11:45:17 AM Jan 3, 2012	 * @description 判断当前的用户是否已经对该look投票过	 * @param userId	 * @param lookId	 * @return	 */	public abstract Boolean isUserVotedLook(Long userId, Long lookId);	/**	 * @company 新和新拓（北京）科技有限公司	 * @author newnewmac	 * @createDate 11:45:36 AM Jan 3, 2012	 * @description 对Look进行投票	 * @param userId	 * @param photoIndex	 * @return vote ID	 */	public abstract Long voteLook(Long userId, Long lookId, int photoIndex);}