package cn.npnt.tiaps.service.impl;import java.util.List;import cn.npnt.tiaps.dao.BrandDao;import cn.npnt.tiaps.entity.Brand;import cn.npnt.tiaps.service.BrandService;public class BrandServiceImpl implements BrandService {	private BrandDao brandDao;			public BrandDao getBrandDao() {		return brandDao;	}	public void setBrandDao(BrandDao brandDao) {		this.brandDao = brandDao;	}	@Override	public List<Brand> getAll() {		return brandDao.findAll(Brand.class, "id");	}	@Override	public Brand getByPK(long id) {		return brandDao.findByPK(Brand.class, id);	}}