package cn.npnt.tiaps.service.impl;import java.util.ArrayList;import java.util.List;import cn.npnt.tiaps.dao.CriterionDao;import cn.npnt.tiaps.entity.Criterion;import cn.npnt.tiaps.service.CriterionService;import cn.npnt.tiaps.vo.CriterionVO;public class CriterionServiceImpl implements CriterionService {	private CriterionDao criterionDao;		public CriterionDao getCriterionDao() {		return criterionDao;	}	public void setCriterionDao(CriterionDao criterionDao) {		this.criterionDao = criterionDao;	}	@Override	public List<CriterionVO> getAll() {		List<CriterionVO> voList = null;		List<Criterion> list = criterionDao.findAll(Criterion.class, "id");		if(list != null && list.size() > 0){			CriterionVO vo = null;			voList = new ArrayList<CriterionVO>();			for(Criterion c : list){				vo = new CriterionVO(); 				vo.setCategory(c.getCategory());				vo.setHotter(c.getHotter());				vo.setTitle(c.getTitle());				vo.setId(c.getId());				voList.add(vo);			}		}		return voList;	}	@Override	public Criterion getByPK(long id) {		return criterionDao.findByPK(Criterion.class, id);	}}