package cn.npnt.tiaps.service.impl;import java.util.ArrayList;import java.util.List;import cn.npnt.tiaps.dao.StyleDao;import cn.npnt.tiaps.entity.Style;import cn.npnt.tiaps.service.StyleService;import cn.npnt.tiaps.vo.StyleVO;/** * @company 新和新拓（北京）科技有限公司 * @author Recoba Gan * @createDate 2011 2011-12-18 下午9:04:55 * @description 风格业务接口实现类 */public class StyleServiceImpl implements StyleService {	private StyleDao styleDao;			public StyleDao getStyleDao() {		return styleDao;	}	public void setStyleDao(StyleDao styleDao) {		this.styleDao = styleDao;	}		/*	 * (non-Javadoc)	 * @see cn.npnt.tiaps.service.StyleService#getAll()	 */	@Override	public List<Style> getAll() {		return styleDao.findAll(Style.class, "index");	}	/*	 * (non-Javadoc)	 * @see cn.npnt.tiaps.service.StyleService#getByPK(long)	 */	@Override	public Style getByPK(long id) {		return styleDao.findByPK(Style.class, id);	}		@Override	public List<StyleVO> getAllVO() {		List<StyleVO> voList = null;		List<Style> list = styleDao.findAll(Style.class, "index");		if(list != null && list.size() > 0){			StyleVO vo = null;			voList = new ArrayList<StyleVO>();			for(Style s : list){				vo = new StyleVO(s.getId(),s.getTitle());				voList.add(vo);			}		}		return voList;	}}