package cn.npnt.tiaps.service.impl;import java.util.ArrayList;import java.util.List;import cn.npnt.tiaps.dao.TrendDao;import cn.npnt.tiaps.entity.Trend;import cn.npnt.tiaps.service.TrendService;import cn.npnt.tiaps.vo.TrendVO;/** * @company 新和新拓（北京）科技有限公司 * @author Recoba Gan * @createDate 2011 2011-12-18 下午4:09:24 * @description 活动的主题业务接口实现类 */public class TrendServiceImpl implements TrendService {	private TrendDao trendDao;		public TrendDao getTrendDao() {		return trendDao;	}	public void setTrendDao(TrendDao trendDao) {		this.trendDao = trendDao;	}	/*	 * (non-Javadoc)	 * @see cn.npnt.tiaps.service.TrendService#getAllTrend()	 */	@Override	public List<Trend> getAll() {		return trendDao.findAll(Trend.class, "id");	}	@Override	public Trend getByPK(Long pk) {		return trendDao.findByPK(Trend.class, pk);	}	@Override	public List<TrendVO> getAllVO() {		List<TrendVO> voList = null;		List<Trend> list = trendDao.findAll(Trend.class, "id");		if(list != null && list.size() > 0){			voList = new ArrayList<TrendVO>();			TrendVO vo = null;			for(Trend t : list){				vo = new TrendVO(t.getId(),t.getTitle());				voList.add(vo);			}		}		return voList;	}}