package cn.npnt.tiaps.service.impl;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import cn.npnt.tiaps.dao.VoteDao;import cn.npnt.tiaps.service.VoteService;/** * @company 新和新拓（北京）科技有限公司 * @author newnewmac * @createDate 12:21:14 PM Jan 3, 2012 * @description 投票管理实现类 */public class VoteServiceImpl implements VoteService {	private VoteDao voteDao;	@Autowired	public void setVoteDao(VoteDao voteDao) {		this.voteDao = voteDao;	}	/* (non-Javadoc)	 * @see cn.npnt.tiaps.service.VoteService#findLookVotedData(java.lang.Long)	 */	@Override	public List<Integer> findLookVotedData(Long lookId) {		return voteDao.findLookVotedData(lookId);	}	/* (non-Javadoc)	 * @see cn.npnt.tiaps.service.VoteService#isUserVotedLook(java.lang.Long, java.lang.Long)	 */	@Override	public Boolean isUserVotedLook(Long userId, Long lookId) {		return voteDao.isUserVotedLook(userId, lookId);	}	/* (non-Javadoc)	 * @see cn.npnt.tiaps.service.VoteService#voteLook(java.lang.Long, java.lang.Long, int)	 */	@Override	public Long voteLook(Long userId, Long lookId, int photoIndex) {		return voteDao.voteLook(userId, lookId, photoIndex);	}	}