package cn.npnt.tiaps.vo;public class CriterionVO {	private long id;	private String category;	private String title;	private short hotter;		public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public String getCategory() {		return category;	}	public void setCategory(String category) {		this.category = category;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public short getHotter() {		return hotter;	}	public void setHotter(short hotter) {		this.hotter = hotter;	}}